import React from 'react';
import { Container, Card, Row, Image } from 'react-bootstrap';
import { StockTypes } from './data';

interface StockProps {
    info: StockTypes;
};

const StatisticsWidget = ({ info }: StockProps) => {
    let img_url = "https://storage.googleapis.com/iex/api/logos/" + info.symbol + ".png";
    return (
        <Card className="text-center">
            <Image src={img_url} alt="showing stock symbol" fluid />
            <Card.Body className="card-body">
                <div className="d-flex align-items-start">
                    <div className="flex-1 text-end align-self-center">
                        <h3 className="mt-0">
                            <span>
                                {info.change} and {info.percent_change}
                            </span>
                        </h3>
                        <p className="text-muted mb-0">{info.name}</p>
                    </div>
                </div>
                {/* {info.symbol} and {info.change} and {info.percent_change}
                <Card.Title className="card-title">{info.name}</Card.Title>
                <Card.Subtitle className="text-muted">{info.price}</Card.Subtitle>
                {info.symbol} and {info.change} and {info.percent_change} */}
            </Card.Body>
        </Card>
    );
};

export default StatisticsWidget;
