import React from 'react';
import { Card } from 'react-bootstrap';
import { StockTypes } from './data';
import './widget.css'
import { FaArrowDown, FaArrowUp } from "react-icons/fa"; // up and down arrow icons

interface StockProps {
    info: StockTypes;
};

const StatisticsWidget = ({ info }: StockProps) => {
    let img_url = "https://storage.googleapis.com/iex/api/logos/" + info.symbol + ".png";
    const direction = (parseInt(info.change) < 0) ? 'down' : 'up';
    return (
        <Card className="card">
            <div className="card-logo">
                <img src={img_url} alt="stock img" />
            </div>
            <Card.Body className="card-info">
                <div className="card-symbol">{info.symbol}</div>
                <Card.Subtitle className="card-name">{info.name}</Card.Subtitle>
                <div className="d-flex flex-column text-right">
                    <div className="card-price">{info.price}</div>
                </div>
                <div className="d-flex justify-content-end align-items-center">
                    {direction === 'down' ? <FaArrowDown className="card-arrow-down" /> : <FaArrowUp className="card-arrow-up" />}
                    <div className={`card-change ${direction}`}>{info.change}</div>
                    <div className={`card-percent ${direction}`}>{info.percent_change}</div>
                </div>
            </Card.Body>
        </Card>
    );
};

export default StatisticsWidget;
