import { useState, useEffect } from 'react';
// import { Row, Col } from 'react-bootstrap';
import StatisticsWidget from './widget';
import axios from 'axios';
// import './App.css';
const stocks = [{ name: 'aapl' }, { name: 'nflx' }]; // { name: 'goog' }, { name: 'amzn' }, { name: 'tsla' }

interface StatisticsProps {
  symbol?: string;
  name?: string;
  price?: string;
  change?: string;
  percent_change?: string;
};

function App() {
  const [data, setData] = useState<StatisticsProps[]>([]);

  useEffect(() => {
    var stock_info: StatisticsProps[] = [];
    const fetchData = async () => {
      try {
        await Promise.all(stocks.map(async (stock) => {
          const res = await axios.get(`/api/stock_info?stock=${stock.name}`);
          stock_info.push(res.data);
          console.log(res.data);
        }))
      } catch (e) {
        console.log(e);
      }
    };
    fetchData();
    setData(stock_info);
  }, [data]);

  return (
    <>
      {(data || []).map(({ symbol, name, price, change, percent_change }: StatisticsProps) =>
        <StatisticsWidget symbol={symbol} name={name} price={price} change={change} percent_change={percent_change} />
      )}
    </>
  );
}

export default App;

