import { useState, useEffect } from 'react';
// import { Row, Col } from 'react-bootstrap';
import StatisticsWidget from './widget';
import axios from 'axios';
import { StockTypes } from './data';
// import './App.css';
const stocks = [{ name: 'aapl' }, { name: 'nflx' }]; // { name: 'goog' }, { name: 'amzn' }, { name: 'tsla' }

const App = () => {
  const [data, setData] = useState<StockTypes[]>([]);

  useEffect(() => {
    // var stock_info: StatisticsProps[] = [];
    const fetchData = async () => {
      try {
        // await Promise.all(stocks.map(async (stock) => {
        //   const res = await axios.get(`/api/stock_info?stock=${stock.name}`);
        //   // stock_info.push(res.data);
        //   setData(res.data);
        //   console.log(res.data);
        // }));
        // const res = await axios.get(`/api/stock_info?stock=aapl`);
        // setData(res.data);
        const req = stocks.map((stock) => axios.get(`/api/stock_info?stock=${stock.name}`));
        const res = await Promise.all(req);
        const combinedData = res.reduce<StockTypes[]>((result, response) => {
          return [...response.data];
        }, []);
        setData(combinedData);
      } catch (e) {
        console.log(e);
      }
    };
    fetchData();
    // setData(stock_info);
  }, []);

  return (
    <>
      {/* {data && <StatisticsWidget info={data[0]} />} */}
      {data.map((stock, index) => <li key={index}><StatisticsWidget info={stock} /> </li>)}
      {/* {data.map((stock) => {
        return (
          <StatisticsWidget info={stock} />
        );
      })} */}
    </>
  );
};

export default App;

