import { useState, useEffect } from 'react';
import { Row } from 'react-bootstrap';
import StatisticsWidget from './widget';
import axios from 'axios';
import { StockTypes } from './data';
import './App.css';
const stocks = ['aapl', 'nflx', 'goog', 'amzn', 'tsla']; // 'goog', 'amzn', 'tsla'

const App = () => {
  const [data, setData] = useState<StockTypes[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        var list = [];
        for (const stock of stocks) {
          const res = await axios.get(`/api/stock_info?stock=${stock}`);
          list.push(res.data);
        }
        setData(list);
      } catch (e) {
        console.log(e);
      }
    };
    fetchData();;
  }, []);

  return (
    <>
      <div className="App">
        {data && data.map((stock) =>
          <StatisticsWidget info={stock} />
        )}
      </div>
    </>
  );
};

export default App;

