import { useState, useEffect } from 'react';
import { Row, Col } from 'react-bootstrap';
import StatisticsWidget from './widget';
import axios from 'axios';
// import './App.css';

function App() {
  // const [img, setImg] = useState('');

  // const fetchData = async () => {
  //   const res = await axios({
  //     method: 'get',
  //     url: `https://storage.googleapis.com/iex/api/logos/AAPL.png`,
  //     responseType: 'blob',
  //     headers: {
  //       'Content-Type': 'application/json',
  //       'Access-Control-Allow-Origin': '*',
  //       'Access-Control-Allow-Credentials': true,
  //       'mode': 'no-cors'
  //     }
  //   });
  //   const imageObjectURL = URL.createObjectURL(res.data);
  //   console.log(imageObjectURL);
  //   setImg(imageObjectURL);
  // };
  const [data, setData] = useState({});
  const [img, setImg] = useState('');
  useEffect(() => {
    //fetchData();
    fetch('/api/stock_info')
      .then(resp => resp.json())
      .then(json => {
        var imgObject = json.img.blob();
        setData(json);
        setImg(URL.createObjectURL(imgObject));
        console.log(json);
        console.log(imgObject);
      })
      .catch(err => console.error('Could not load data', err)); // eslint-disable-line
  }, []);

  return (
    <Row>
      <img src={img} className="Screenshot" alt="showing screen capture" />
      <Col xl={3} md={6}>
        <StatisticsWidget img={img} symbol="aapl" name="Apple" price="80" dollar_difference="23" percent_difference="0.5%" />
      </Col>
      <Col xl={3} md={6}>
        <StatisticsWidget img={img} symbol="tsla" name="Tesla" price="300" dollar_difference="50" percent_difference="2.5%" />
      </Col>
      <Col xl={3} md={6}>
        <StatisticsWidget img={img} symbol="amd" name="AMD" price="34" dollar_difference="-12" percent_difference="-3.7%" />
      </Col>
    </Row>
  );
}

export default App;
