import { useState, useEffect } from 'react';
import { Row, Col } from 'react-bootstrap';
import StatisticsWidget from './widget';
import axios from 'axios';
// import './App.css';
const stocks = [{ name: 'aapl' }, { name: 'tsla' }, { name: 'goog' }, { name: 'amzn' }, { name: 'tsla' }];

interface StatisticsProps {
  symbol?: string;
  name?: string;
  price?: string;
  change?: string;
  percent_change?: string;
};

function App() {
  const [data, setData] = useState<StatisticsProps[]>([]);

  // const fetchData = async () => {
  //   stocks.forEach(async (stock) => {
  //     const res = await axios.get(`/api/stock_info?stock=${stock.name}`);

  // };
  useEffect(() => {
    // axios.get<Blob>('/api/stock_info', { responseType: 'blob' })
    //   .then(response => setImg(URL.createObjectURL(response.data)))
    //   .catch(error => console.error(error));
    // fetchData();
    var stock_info: StatisticsProps[] = [];
    stocks.forEach(async (stock) => {
      const res = await axios.get(`/api/stock_info?stock=${stock.name}`);
      stock_info.push(res.data);
    });
    setData(stock_info);
  }, []);

  return (
    <Row>
      {(data || []).map(({ symbol, name, price, change, percent_change }: StatisticsProps) => {
        return (
          <Col xl={6} key={symbol}>
            <StatisticsWidget symbol={symbol} name={name} price={price} change={change} percent_change={percent_change} />
          </Col>
        );
      })}
    </Row>
  );
}

export default App;

